 CREATE TABLE `Badge` (`id` int NOT NULL AUTO_INCREMENT, `mint` int NOT NULL, `address` varchar(255) NOT NULL, `score` int NOT NULL, `seed` decimal(65,30) NOT NULL DEFAULT '1.000000000000000000000000000000', `name` varchar(255) NOT NULL, `imageUrl` text NOT NULL, `createdAt` datetime(3) NOT NULL DEFAULT CURRENT_TIMESTAMP(3), `updatedAt` datetime(3) NOT NULL DEFAULT CURRENT_TIMESTAMP(3), `version` int NOT NULL DEFAULT '1', `rev` int NOT NULL DEFAULT '1', `season` int NOT NULL DEFAULT '1', PRIMARY KEY (`id`) ) ENGINE InnoDB, CHARSET utf8mb4, COLLATE utf8mb4_unicode_ci;